namespace App\Services;

use App\Models\CarInsurance\CardInfo;
use Intervention\Image\ImageManagerStatic as StaticImage;

class ImageService
{
    //测试之用
    static $signature='data:image/png;base64,"
  ';
    /**
     * @param $cardInfoId integer 从cardInfo表中读取base64图片字符串，解码后返回
     */
    public static function getSignatureResponse($signature)
    {
        try{
            $sigPicture = self::getSignaturePicture($signature);

            return $sigPicture->resize(500,400)->response('png');
        }catch(\Exception $e){
            throw $e;
        }
    }

    /**
     * @param $signature
     */
    public static function getSignaturePicture($signature)
    {
        $isTest = config('carInsurance.test');
        if($signature=='') {
            if($isTest) { //测试之用
                $content = base64_decode(str_replace('data:image/png;base64,', '', self::$signature));
            }else { //否则直接报错
                throw new \Exception("签名图片信息为空，错误!");
            }
        } else { //不确定签名的图片类型,有待修正!
            $content = base64_decode(str_replace('data:image/png;base64,', '', $signature));
        }

        return StaticImage::make($content);
    }

    public static function getTab($path, $signature)
    {
        try{
            $sigPicture = self::getSignaturePicture($signature);
            $sigPicture->resize(500, 300);

            $path = self::getTabImgPath(). "$path.png";
            $parent = StaticImage::make($path)->resize(1780,2560)->insert($sigPicture, 'bottom-left', 1000, 100)->response('png');

            return $parent;
        }catch (\Exception $e){
            throw $e;
        }
    }

    public static function noSuchImage()
    {
        $noFound = self::getAppdir(). "/public/static/car-insurance/not-upload.jpg";
        return StaticImage::make($noFound)->response('jpg');
    }

    public static function getTabImgPath()
    {
        $baseDir = self::getAppdir();
        return $baseDir."/public/static/car-insurance/agreement/";
    }

    /**
     * 好像并没有使用
     * @return string
     */
    private static function getAppdir()
    {
        $v = explode(DIRECTORY_SEPARATOR, __DIR__);
        array_pop($v);array_pop($v);
        $baseDir = implode(DIRECTORY_SEPARATOR, $v);
        return $baseDir;
    }

    /**
     * 检查文件是否存在
     */
    public static function fileExists($path)
    {
        $baseDir = self::getAppdir();
        return file_exists($baseDir. $path);
    }
}